Ext.form.Radio=Ext.extend(Ext.form.Checkbox,{inputType:"radio",markInvalid:Ext.emptyFn,clearInvalid:Ext.emptyFn,getGroupValue:function(){var n=this.el.up("form")||Ext.getBody();return n.child("input[name="+this.el.dom.name+"]:checked",!0).value},onClick:function(){if(this.el.dom.checked!=this.checked){var n=this.el.up("form")||Ext.getBody(),t=n.select("input[name="+this.el.dom.name+"]");t.each(function(n){n.dom.id==this.id?this.setValue(!0):Ext.getCmp(n.dom.id).setValue(!1)},this)}}});Ext.reg("radio",Ext.form.Radio)