Ext.menu.DateItem=function(n){Ext.menu.DateItem.superclass.constructor.call(this,new Ext.DatePicker(n),n);this.picker=this.component;this.addEvents("select");this.picker.on("render",function(n){n.getEl().swallowEvent("click");n.container.addClass("x-menu-date-item")});this.picker.on("select",this.onSelect,this)};Ext.extend(Ext.menu.DateItem,Ext.menu.Adapter,{onSelect:function(n,t){this.fireEvent("select",this,t,n);Ext.menu.DateItem.superclass.handleClick.call(this)}})