Ext.menu.DateMenu=function(n){Ext.menu.DateMenu.superclass.constructor.call(this,n);this.plain=!0;var t=new Ext.menu.DateItem(n);this.add(t);this.picker=t.picker;this.relayEvents(t,["select"]);this.on("beforeshow",function(){this.picker&&this.picker.hideMonthPicker(!0)},this)};Ext.extend(Ext.menu.DateMenu,Ext.menu.Menu,{cls:"x-date-menu",beforeDestroy:function(){this.picker.destroy()}})